// Format Admin Meeting Data - Robust Implementation
// This node transforms Google Sheets data into structured meeting data for the admin panel

try {
  // Get input data from Google Sheets
  const inputData = $input.all();
  console.log('Raw input data received:', inputData.length, 'items');

  // Handle empty input
  if (!inputData || inputData.length === 0) {
    console.log('No data received from Google Sheets');
    return {
      json: {
        success: true,
        meetings: [],
        statistics: {
          totalCount: 0,
          pendingCount: 0,
          approvedCount: 0,
          rejectedCount: 0,
          rescheduledCount: 0,
          cancelledCount: 0
        },
        lastUpdated: new Date().toISOString(),
        message: 'No meetings found in the system'
      }
    };
  }

  // Log first item structure for debugging
  const firstItem = inputData[0];
  const sampleData = firstItem.json || firstItem;
  console.log('Sample data structure:', Object.keys(sampleData));
  console.log('First item:', JSON.stringify(sampleData, null, 2));

  // Helper function to safely get values from different possible column names
  function safeGetValue(rowData, ...possibleKeys) {
    for (const key of possibleKeys) {
      if (rowData[key] !== undefined && rowData[key] !== null && rowData[key] !== '') {
        return String(rowData[key]).trim();
      }
    }
    return '';
  }

  // Helper function to safely convert to lowercase
  function safeLowerCase(value, defaultValue = '') {
    if (!value) return defaultValue;
    return String(value).toLowerCase().trim();
  }

  // Process each row of data
  const processedMeetings = [];

  for (let i = 0; i < inputData.length; i++) {
    const item = inputData[i];
    const rowData = item.json || item;

    // Skip header rows or empty rows
    const requestId = safeGetValue(rowData, 'Request_ID', 'requestId', 'Request ID', 'ID');
    if (!requestId || requestId === 'Request_ID' || requestId === 'requestId') {
      console.log(`Skipping row ${i + 1}: header or empty row`);
      continue;
    }

    // Extract and clean data
    const meeting = {
      // Core identification
      requestId: requestId,
      timestamp: safeGetValue(rowData, 'Timestamp', 'timestamp', 'Created_Date', 'createdDate') || new Date().toISOString(),

      // User information
      userName: safeGetValue(rowData, 'User_Name', 'userName', 'User Name', 'Name') || 'Unknown User',
      userEmail: safeGetValue(rowData, 'User_Email', 'userEmail', 'User Email', 'Email'),
      userPhone: safeGetValue(rowData, 'User_Phone', 'userPhone', 'User Phone', 'Phone'),
      userCompany: safeGetValue(rowData, 'User_Company', 'userCompany', 'User Company', 'Company'),
      userPosition: safeGetValue(rowData, 'User_Position', 'userPosition', 'User Position', 'Position'),

      // Meeting details
      meetingPurpose: safeGetValue(rowData, 'Meeting_Purpose', 'meetingPurpose', 'Meeting Purpose', 'Purpose') || 'Meeting Request',
      meetingDescription: safeGetValue(rowData, 'Meeting_Description', 'meetingDescription', 'Meeting Description', 'Description'),
      preferredDate: safeGetValue(rowData, 'Preferred_Date', 'preferredDate', 'Preferred Date', 'Date'),
      preferredTime: safeGetValue(rowData, 'Preferred_Time', 'preferredTime', 'Preferred Time', 'Time'),
      duration: safeGetValue(rowData, 'Duration_Minutes', 'duration', 'Duration', 'estimatedDuration') || '60',
      meetingType: safeLowerCase(safeGetValue(rowData, 'Meeting_Type', 'meetingType', 'Meeting Type', 'Type'), 'online'),
      location: safeGetValue(rowData, 'Location', 'location'),

      // Status and priority
      status: safeLowerCase(safeGetValue(rowData, 'Status', 'status'), 'pending'),
      priority: safeLowerCase(safeGetValue(rowData, 'Priority', 'priority', 'urgency'), 'normal'),

      // Additional fields
      additionalNotes: safeGetValue(rowData, 'Additional_Notes', 'additionalNotes', 'Additional Notes', 'Notes'),
      adminResponse: safeGetValue(rowData, 'Admin_Response', 'adminResponse', 'Admin Response'),
      confirmedDate: safeGetValue(rowData, 'Confirmed_Date', 'confirmedDate', 'Confirmed Date'),
      confirmedTime: safeGetValue(rowData, 'Confirmed_Time', 'confirmedTime', 'Confirmed Time'),
      calendarEventId: safeGetValue(rowData, 'Calendar_Event_ID', 'calendarEventId', 'Calendar Event ID'),
      meetingLink: safeGetValue(rowData, 'Meeting_Link', 'meetingLink', 'Meeting Link'),
      lastUpdated: safeGetValue(rowData, 'Last_Updated', 'lastUpdated', 'Last Updated') || new Date().toISOString(),
      createdDate: safeGetValue(rowData, 'Created_Date', 'createdDate', 'Created Date') || new Date().toISOString()
    };

    // Add formatted display fields
    meeting.displayDate = meeting.preferredDate ? 
      formatDate(meeting.preferredDate) : 'Not specified';
    meeting.displayTime = meeting.preferredTime || 'Not specified';
    meeting.urgencyDisplay = getUrgencyDisplay(meeting.priority);
    meeting.statusDisplay = getStatusDisplay(meeting.status);
    meeting.meetingTypeDisplay = getMeetingTypeDisplay(meeting.meetingType);

    // Add to processed meetings
    processedMeetings.push(meeting);
    console.log(`Processed meeting ${i + 1}: ${meeting.userName} - ${meeting.meetingPurpose}`);
  }

  // Helper functions for display formatting
  function formatDate(dateString) {
    try {
      const date = new Date(dateString);
      if (isNaN(date.getTime())) {
        return dateString; // Return original if not a valid date
      }
      return date.toLocaleDateString('en-US', {
        year: 'numeric',
        month: 'short',
        day: 'numeric'
      });
    } catch (error) {
      return dateString;
    }
  }

  function getUrgencyDisplay(priority) {
    const urgencyMap = {
      'low': 'ðŸŸ¢ Low',
      'normal': 'ðŸŸ¡ Normal',
      'high': 'ðŸŸ  High',
      'urgent': 'ðŸ”´ Urgent'
    };
    return urgencyMap[priority] || 'ðŸŸ¡ Normal';
  }

  function getStatusDisplay(status) {
    const statusMap = {
      'pending': 'â³ Pending',
      'approved': 'âœ… Approved',
      'rejected': 'âŒ Rejected',
      'rescheduled': 'ðŸ“… Rescheduled',
      'cancelled': 'ðŸš« Cancelled'
    };
    return statusMap[status] || 'â³ Pending';
  }

  function getMeetingTypeDisplay(meetingType) {
    const typeMap = {
      'online': 'ðŸ’» Online',
      'offline': 'ðŸ¢ In-Person',
      'hybrid': 'ðŸ”„ Hybrid',
      'phone': 'ðŸ“ž Phone'
    };
    return typeMap[meetingType] || 'ðŸ’» Online';
  }

  // Calculate statistics
  const totalCount = processedMeetings.length;
  const pendingCount = processedMeetings.filter(m => m.status === 'pending').length;
  const approvedCount = processedMeetings.filter(m => m.status === 'approved').length;
  const rejectedCount = processedMeetings.filter(m => m.status === 'rejected').length;
  const rescheduledCount = processedMeetings.filter(m => m.status === 'rescheduled').length;
  const cancelledCount = processedMeetings.filter(m => m.status === 'cancelled').length;

  // Sort meetings by timestamp (newest first)
  try {
    processedMeetings.sort((a, b) => {
      const dateA = new Date(a.timestamp);
      const dateB = new Date(b.timestamp);
      return dateB.getTime() - dateA.getTime();
    });
  } catch (sortError) {
    console.log('Sorting error (using original order):', sortError.message);
  }

  // Prepare final result
  const result = {
    json: {
      success: true,
      meetings: processedMeetings,
      statistics: {
        totalCount,
        pendingCount,
        approvedCount,
        rejectedCount,
        rescheduledCount,
        cancelledCount
      },
      lastUpdated: new Date().toISOString(),
      message: `Successfully processed ${totalCount} meetings`,
      metadata: {
        processedAt: new Date().toISOString(),
        sourceRows: inputData.length,
        validMeetings: totalCount,
        skippedRows: inputData.length - totalCount
      }
    }
  };

  console.log('Processing complete:');
  console.log('- Total rows received:', inputData.length);
  console.log('- Valid meetings processed:', totalCount);
  console.log('- Pending:', pendingCount, 'Approved:', approvedCount, 'Rejected:', rejectedCount);

  return result;

} catch (error) {
  console.error('Error in Format Admin Meeting Data:', error);

  // Return error response
  return {
    json: {
      success: false,
      error: error.message,
      meetings: [],
      statistics: {
        totalCount: 0,
        pendingCount: 0,
        approvedCount: 0,
        rejectedCount: 0,
        rescheduledCount: 0,
        cancelledCount: 0
      },
      lastUpdated: new Date().toISOString(),
      message: 'Error processing meeting data: ' + error.message
    }
  };
}
